         X[M] mapping skillMap;
string selector;

\$skillsDB = \$\{Allegory:Data:SkillsDB\};

if(\$skillMap) \{
    skillMap = \$skillMap;
\} else \{
    skillMap = ([ ]);
\}

if(!\$subclass) return skillMap;

\$subclass = replace_strings(\$subclass, " ", "-");
if(\$subclass == "general" \|\| \$subclass == "languages") \{
    selector = "class:" + \$subclass; 
\} else \{
    \$class = Call(\$skillsDB, "get-class", \$subclass: \$subclass);
    selector = "class:" + \$class + ":" + \$subclass;
\}

\$temp = map_indices(Get(\$skillsDB, selector)["skills"]);

for(\$i = 0; \$i \< sizeof(\$temp); \$i++) \{
   if(skillMap[\$temp[\$i]]) \{
       skillMap[\$temp[\$i]] += Get(\$skillsDB, selector)["skills"][\$temp[\$i]];
   \} else \{
       skillMap[\$temp[\$i]] = Get(\$skillsDB, selector)["skills"][\$temp[\$i]];
   \}
\}

return skillMap;
