Core:Properties.Core:Property:
         X[M] /* check if the meat is roasted already */
if(\$this."trait:cooked") \{
   EmitTo(\$actor, "The fish has already been cooked."); 
   return FALSE;
   \}

if(!\$iob) \{
 EmitTo(\$actor, "You will need something to roast "+Describe(this, nil, \$actor)+" in or on.");
 return FALSE;
\}

\$heatsource = nil;
for(\$i=0;\$i\<sizeof(\$iob);\$i++) \{
 if(NRefOb(\$iob[\$i])."cooking:heatsource")
  \$heatsource = 1;
\}
if(!\$heatsource) \{
 EmitTo(\$actor, "You cannot cook over "+Describe(\$iob)+". Maybe it isn't hot enough?");
 return FALSE;
\}


/* emit the roasting */
 EmitTo(\$actor, Describe(\$this, nil, \$actor)+" slowly roasts over the fire, sending wafts of mouthwatering scents into the air.");
 EmitIn(\$actor."base:environment", Describe(\$this)+" slowly roasts over the fire, sending wafts of mouthwatering scents into the air. "+ Describe(\$actor)+" gradually turns the fish to allow the fire to mark all sides of it.", \$actor);

/* roast the meat */
\$this."trait:cooked" = "roasted";
\$this."trait:cooked:desc" = "The lean meat has been roasted in earthy seasonings with a crispy, flaky skin.";
\$this."trait:smell" = "fish with a hint of seasoning";
\$this."trait:taste" = "mildly fishy, but enhanced by the addition of herbal seasonings. The tender, flaky meat falls apart in your mouth";
\$this."trait:details:default:roasted" = TRUE;
\$this."trait:details:default:cooked" = TRUE;


if(\$this."base:environment" != \$actor) \{
 EmitTo(\$actor, Describe(\$this, nil, \$actor)+" slips out of your hands, and onto the ground. Oops.");
 EmitIn(\$actor."base:environment", Describe(\$this)+" slips out of "+Describe(\$actor)+"'s hands, and onto the ground. Oops.", \$actor);
 Slay(\$this);
 return FALSE;
\}

return FALSE;

         X[M] if(\$this."base:environment" != \$actor) \{
  EmitTo(\$actor, "You cannot preserve a fish which is not in your possession.");
  return FALSE;
\}

if(!\$dob \|\| sizeof(\$dob) != 1) \{
  EmitTo(\$actor, "You may only preserve a single fish at a time.");
  return FALSE;
\}

\$orig = \$this;
\$mount = Spawn( \$\{Allegory:Props:Animals:Fish:UrFishMountable\} );

if(\$orig."trait:color") \{ \$mount."trait:color" = \$orig."trait:color"; \}
if(\$orig."trait:brief") \{ \$mount."trait:brief" = \$orig."trait:brief"; \}
if(\$orig."trait:size:sentence") \{ \$mount."trait:size:sentence" = \$orig."trait:size:sentence"; \}
if(\$orig."trait:body") \{ \$mount."trait:body" = \$orig."trait:body"; \}
if(\$orig."trait:eyes") \{ \$mount."trait:eyes" = \$orig."trait:eyes"; \}
if(\$orig."trait:analfin") \{ \$mount."trait:analfin" = \$orig."trait:analfin"; \}
if(\$orig."trait:tailfin") \{ \$mount."trait:tailfin" = \$orig."trait:tailfin"; \}
if(\$orig."trait:softdorsal") \{ \$mount."trait:softdorsal" = \$orig."trait:softdorsal"; \}
if(\$orig."trait:spinousdorsal") \{ \$mount."trait:spinousdorsal" = \$orig."trait:spinousdorsal"; \}
if(\$orig."trait:pectoralfin") \{ \$mount."trait:pectoralfin" = \$orig."trait:pectoralfin"; \}
if(\$orig."trait:ventralfin") \{ \$mount."trait:ventralfin" = \$orig."trait:ventralfin"; \}
if(\$orig."trait:finlet") \{ \$mount."trait:finlet" = \$orig."trait:finlet"; \}
if(\$orig."trait:gills") \{ \$mount."trait:gills" = \$orig."trait:gills"; \}
if(\$orig."trait:head") \{ \$mount."trait:head" = \$orig."trait:head"; \}
if(\$orig."trait:mouth") \{ \$mount."trait:mouth" = \$orig."trait:mouth"; \}
if(\$orig."trait:opercles") \{ \$mount."trait:opercles" = \$orig."trait:opercles"; \}
if(\$orig."trait:preopercles") \{ \$mount."trait:preopercles" = \$orig."trait:preopercles"; \}
if(\$orig."trait:scales") \{ \$mount."trait:scales" = \$orig."trait:scales"; \}
if(\$orig."trait:scutes") \{ \$mount."trait:scutes" = \$orig."trait:scutes"; \}
if(\$orig."trait:snout") \{ \$mount."trait:snout" = \$orig."trait:snout"; \}
\$mount."trait:state" = "preserved";

\$result = ironclaw::roll(\$roller: \$actor, \$skills: (\{"skill:hunting:skinning", "skill:general:fishing" \}), \$bonus: 0);
if(\$result \> 6) \{
  \$result = ironclaw::roll(\$roller: \$actor, \$skills: (\{"skill:hunting:skinning", "skill:general:fishing" \}), \$bonus: 0);
  if(\$result \< 6) \$result = 6;
\}
\$mount."trait:quality" = \$result;

EmitTo(\$actor, Describe(\$this, nil, \$actor) + " becomes frozen in place as you work to preserve it.");
EmitIn(\$actor."base:environment", Describe(\$this) + " becomes frozen in place as it is preserved from rot and decay before being mounted for display.", \$actor);
\$mount."base:environment" = \$actor;
Slay(\$orig);
return TRUE;
