Core:Properties.Core:Property:
         X[M] if(this."trait:name") \{  
  EmitTo(\$actor, Describe(this)+" already has a name. Sheesh.");  
  return TRUE;  
\}  

if(this."base:environment" != \$actor) \{
    EmitTo(\$actor, "You can only name pets that you are holding.");
    return TRUE;
\}                         

\$name = capitalize(\$evoke[1..strlen(\$evoke)-2]);  
/* If we have any punctuation at the end of the command, remove it */  
if(!((\$name[strlen(\$name)-1] \>= 97) \&\& (\$name[strlen(\$name)-1] \<= 122)) \&\& !((\$name[strlen(\$name)-1] \>= 65) \&\& (\$name[strlen(\$name)-1] \<= 90)))  
  \$name = \$name[0..strlen(\$name)-2];  
/* Only take the first word of the name */  
sscanf(\$name, "%s %*s", \$name);  
  
/* Tell the actor that this was successful */  
EmitTo(\$actor, Describe(this)+" will now respond to "+\$name+" and will consider you its master.");  
  
/* Set the name */  
this."trait:name" = \$name;  
/* set the sname */  
Set(this, "details:default:sname:"+lower_case(\$name), 1);  

this."trait:master" = \$actor;                  
  
  
return TRUE;

         X[M] if((\$actor != \$this.master \&\& \$actor == \$this."trait:ownertwo")) \{
    EmitTo(\$actor, "You cannot take " + Describe(\$this)+ " because it is not yours.");
    return FALSE;
\}
return TRUE;

         X[M] /* Check if the actor is the controller. */

if((\$actor == \$this."trait:master")) \{
   if(\$evoke) \{

      /* Identify the command from the actor's evoke. */
      \$command = \$evoke[1..strlen(\$evoke)-2];

       /* Select appropriate emit based on the command. */
      if(\$command == "flap") \{
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " stretches out its wings, flapping them repeatedly while squawking loudly.", "social:third"));
      \} else if(\$command == "caw") \{
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " lifts its head, letting loose a series of raucous cawing and squawking sounds.", "social:third"));
      \} else if(\$command == "bob") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " tilts it's head to the side before bobbing it's body to and fro with a series of little squawks.", "social:third"));
      \} else if(\$command == "dance") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " partially extends its wings while skittering its feet swiftly and turning in place, adding an occasional head bob to the unique avian dance.", "social:third"));
      \} else if(\$command == "hello") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " clacks its beak a few times before squawking loudly, \\"Hello! Hello!\\"", "social:third"));
      \} else if(\$command == "chatter") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " sways its head side to side while cawing raucously, \\"" + oneof("Oh no she didn't!", "Hurhurhur!", "Oh hello there, pretty lady!", "Nope, nope, nope!", "Me likey long time!", "Crackers are so gooooood!", "Pet me, big boy!") + "\\"", "social:third"));
      \} else if(\$command == "yes") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " bobs its head up and down, repeatedly nodding in a fashion that sets its whole body in motion.", "social:third"));
      \} else if(\$command == "no") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " animatedly shakes its head side to side, nearly going off-balance with the motion.", "social:third"));
      \} else if(\$command == "preen") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " stretches its wings out, fastidiously preening its wing and tail feathers while crooning in self-approval.", "social:third"));
      \} else if(\$command == "laugh") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " clacks its beak for a moment before spreading its wings and tossing its head back. A sound clearly intended to mimic obnoxious laughter fills the area, eventually devolving into loud squawking.", "social:third"));
      \} else if(\$command == "superstar") \{   
         EmitIn(\$this."base:environment"."base:environment", TAG(Describe(\$this) + " flies up to a nearby landing area, spreading it's wings broadly as it calls out jubilantly to the skies while flapping it's wings repeatedly, \\"I'm so fancy!! BWAAAK!\\"", "social:third"));
      \} else \{
         EmitTo(\$actor, "Valid controls for "+Describe(\$this)+" are: flap, caw, bob, dance, hello, chatter, yes, no, preen, laugh, superstar.");\}
   \} else \{
      /* If the actor did not give a command, list commands to the controller. */
      EmitTo(\$actor, "Valid controls for "+Describe(\$this)+" are: flap, caw, bob, dance, hello, chatter, yes, no, preen, laugh, superstar.");\}
   \} else \{
      EmitTo(\$actor, "You cannot control "+Describe(\$this)+".");
\}
return FALSE;
