Core:Properties.Core:Property:
         X[M] EmitTo(\$actor, "You begin to enter the massive wooden gate.");
EmitIn(\$actor."base:environment", Describe(\$actor)+" leaves through the massive wooden gate.", \$actor);
\$actor."base:environment" = \$\{Allegory:Setting:Rinascita:Buildings:Northeast:Colosseum:DuelingArena\};
\$actor."base:proximity" = NewNRef(\$\{Allegory:Setting:Rinascita:Buildings:Northeast:Colosseum:DuelingArena\}, "door-northwest");
EmitIn(\$actor."base:environment", Describe(\$actor)+" arrives through the massive wooden gate.", \$actor);
EmitTo(\$actor, UnSAM(\$"\"));

         X[M] \$door = NRefDetail(\$target);
/* The actor is targeting the southeast door. */
if(\$door == "door-southeast") \{
EmitTo(\$actor, "You are entering a danger area. By entering, you automatically consent to possible death for "+Describe(\$actor)+".");
interaction::question(\$question: "Are you sure you wish to continue?", \$env: "TRUE", \$expires: 60, \$yes_ob: \$this, \$yes_fun: "teleport", \$no: "You decide it would be best to stay here.", \$expire: "You decide it would be best to stay here.", \$par: ([ "actor": \$actor ]));
return FALSE;
\} else \{
return TRUE; \}
return TRUE;

         X[M] /* moving between no partition (room) and luxury box (partition) via the gate */ 
 
/* Check that actor is trying to go back and forth between partitions... */ 
if((\$startpartition == "in the luxury box" \&\& \$targetpartition == nil) \|\| (\$startpartition == nil \&\& \$targetpartition == "in the luxury box")) \{ 
  /* Check to see if the gate is closed... */ 
  if(\$actor."base:environment"."details:gate:exit:closed") \{ 
    /* The gate is closed. Stop the actor and tell them it is closed... */ 
    EmitTo(\$actor, Describe(NewNRef(\$actor."base:environment", "gate"))+" is currently closed."); 
    return FALSE; 
  \} else \{ 
    /* The gate is open. Now check that the actor is a knight or peer... */ 
    if(\$actor."rank:rank" \>= 9)\{ 
      /* The actor is a knight or peer. Allow them to go through... */ 
      EmitTo(\$actor, "A guard moves to the side, allowing you to step through "+Describe(NewNRef(\$actor."base:environment", "gate"))+"."); 
      EmitIn(\$actor."base:environment", "A guard moves to the side, allowing "+Describe(\$actor)+", to step through "+Describe(NewNRef(\$actor."base:environment", "gate"))+".", \$actor); 
    return TRUE; 
    \} else \{ 
      /* The actor is not a knight or peer. Stop the actor and tell them a guard stopped them... */ 
      EmitTo(\$actor, "A guard moves in front of you, preventing you from stepping through "+Describe(NewNRef(\$actor."base:environment", "gate"))+"."); 
      EmitIn(\$actor."base:environment", "A guard moves in front of "+Describe(\$actor)+", preventing "+\$actor."trait:objective"+" from stepping through "+Describe(NewNRef(\$actor."base:environment", "gate"))+".", \$actor); 
   return FALSE; 
   \} 
  \} 
\} 
 
 
/* unless there's some other reason, don't allow them to change partition */ 
return FALSE;

         X[M] /* displays any PCs in the box */ 
int i, sz; 
 
\$list = Match(this, "pc"); 
sz = sizeof(\$list); 
 
for(i=0;i\<sz;i++) \{ 
  if(NRefOb(\$list[i])."room:partition" != "in the luxury box") \{ 
    /* remove anyone not in the box partition from the list */ 
    \$list -= (\{ \$list[i] \}); 
    i--; sz--; 
  \} else if(NRefOb(\$list[i])."base:proximity" 
  \&\& NRefOb(NRefOb(\$list[i])."base:proximity") == this 
  \&\& NRefDetail(NRefOb(\$list[i])."base:proximity") == "box") \{ 
    /* also remove anyone who is in prox with the box, so they don't show up twice */ 
    \$list -= (\{ \$list[i] \}); 
    i--; sz--; 
  \} 
\} 
 
/* don't show anything if there is nobody in the box */ 
if(!sizeof(\$list)) \{ 
  return nil; 
\} 
 
/* show who is in the box */ 
return "Inside the luxury box you can see "+Describe(\$list, \$actor, \$actor)+". ";
