Core:Properties.Core:Property:
         X[M] if((NRefDetail(\$what[0]) != "portcullis" \&\& NRefDetail(\$what[0]) != "exit-north") \|\| \$actor == \$this) \{  
    return TRUE;  
\}  

EmitTo(\$actor, Describe(\$what[0]) + " is too high for you to close that way.");  

return FALSE;

         X[M] \$gate = NewNRef(\$this, "portcullis");  
\$door = NewNRef(\$this, "exit-north"); 

if(NRefDetail(\$dob[0]) != "winch-balcony") \{  
    return TRUE;  
\}  

if(\$this."details:exit-north:exit:closed") \{  
    EmitTo(\$actor, Describe(\$gate) + " is already closed.");  
    return FALSE;  
\}  

EmitTo(\$actor, Describe(\$gate) + " swiftly slides down the two grooves in the stockade fence, and lands with a resounding crash!");  
EmitIn(\$actor."base:environment", Describe(\$gate) + " swiftly slides down the two grooves in the stockade fence, and lands with a resounding crash!", \$actor);  

Act(\$this, "close", \$what: \$door, \$silent: TRUE); 
Act(\$this, "close", \$what: \$gate, \$silent: TRUE); 

return FALSE;

         X[M] \$gate = NewNRef(\$this, "portcullis");  
\$door = NewNRef(\$this, "exit-north"); 

if(NRefDetail(\$dob[0]) != "winch-balcony") \{  
    return TRUE;  
\}  

if(!\$this."details:exit-north:exit:closed") \{  
    EmitTo(\$actor, Describe(\$gate) + " is already open.");  
    return FALSE;  
\}  

EmitTo(\$actor, Describe(\$gate) + " gives a slight screech against the grooves in the stockade fence as the thick chains gradually lift the heavy iron gate.");  
EmitIn(\$actor."base:environment", Describe(\$gate) + " gives a slight screech against the grooves in the stockade fence as the thick chains gradually lift the heavy iron gate.", \$actor);  

Act(\$this, "open", \$what: \$door, \$silent: TRUE);  
Act(\$this, "open", \$what: \$gate, \$silent: TRUE);  

return FALSE;
