Core:Properties.Core:Property:
         X[M] /* The player wants to walk on this walking ball */
/* \$result = Call(\$\{IronClaw:Lib:UrPeople:System\}, "roll", \$dicearray: (\{ \$actor."trait:agility", \$actor."trait:speed", \$actor."skill:career:entertainer", \$actor."skill:career:acrobat" \})); */

\$result = ironclaw::make_opposed_roll(\$val1: 12, \$val2: Int(\$actor."trait:agility")+Int(\$actor."trait:speed")+Int(\$actor."skill:career:entertainer")+Int(\$actor."skill:career:acrobat"), \$opposed_roll_range: 6);
\$result += 6;


/* Give output depending how the player rolled */
switch(\$result) \{
  case 12:
    if((\$description = Get(\$this, "descriptions:walking:12")) \&\& (\$description)) break;
  case 11:
    if((\$description = Get(\$this, "descriptions:walking:11")) \&\& (\$description)) break;
  case 10:
    if((\$description = Get(\$this, "descriptions:walking:10")) \&\& (\$description)) break;
  case 9:
    if((\$description = Get(\$this, "descriptions:walking:9")) \&\& (\$description)) break;
  case 8:
    if((\$description = Get(\$this, "descriptions:walking:8")) \&\& (\$description)) break;
  case 7:
    if((\$description = Get(\$this, "descriptions:walking:7")) \&\& (\$description)) break;
  case 6:
    if((\$description = Get(\$this, "descriptions:walking:6")) \&\& (\$description)) break;
  case 5:
    if((\$description = Get(\$this, "descriptions:walking:5")) \&\& (\$description)) break;
  case 4:
    if((\$description = Get(\$this, "descriptions:walking:4")) \&\& (\$description)) break;
  case 3:
    if((\$description = Get(\$this, "descriptions:walking:3")) \&\& (\$description)) break;
  case 2:
    if((\$description = Get(\$this, "descriptions:walking:2")) \&\& (\$description)) break;
  case 1:
    if((\$description = Get(\$this, "descriptions:walking:1")) \&\& (\$description)) break;
  default:
    \$description = \$this."descriptions:walking:0";
    break;
\}

/* Choose one of the possible descriptions */
if(!\$description) \{
  EmitTo(\$actor, "Error in item.");
  return FALSE;
\}

\$description = copy(\$description[random(sizeof(\$description))]);

if(sizeof(\$description) != 2)
\{
  EmitTo(\$actor, "Error in item return values.");
\}

/* Parse any sam */
\$description[0] = UnSAM(ParseXML(\$description[0]));
\$description[1] = UnSAM(ParseXML(\$description[1]));

/* Replace words in the string */
\$description[0] = replace_strings(\$description[0],
  "ACTOR_POSSESSIVE", \$actor."base:possessive",
  "ACTOR_OBJECTIVE", \$actor."base:objective",
  "ACTOR_PRONOUN", \$actor."base:pronoun",
  "ACTOR", Describe(\$actor),
  "ITEM_FULL", Describe(\$this),
  "ITEM", Describe(\$this, \$actor));
\$description[1] = replace_strings(\$description[1],
  "ACTOR_POSSESSIVE", "your",
  "ACTOR_OBJECTIVE", "your",
  "ACTOR_PRONOUN", "you",
  "ACTOR", "you",
  "ITEM_FULL", Describe(\$this, \$actor, \$actor),
  "ITEM", Describe(\$this, nil, nil, STYLE_NONPOSS));

EmitTo(\$actor, \$description[1]);
EmitIn(\$actor."base:environment", \$description[0], \$actor);

return TRUE;
