Core:Properties.Core:Property:
         X[M] \$item = NRefDetail(\$target);  
 if   (\$item != "crystal") \{return TRUE;\}    
 
if(\$item == "crystal") \{ 
EmitTo(\$actor, "As you focus your attention on the " +Describe(\$target)+ " you briefly catch a glimpse of a raging fire within the small crystal. However as you stare longer at it, you just barely make out the outline of something within the fire. Before you can determine what it is, it's gone along with the fire leaving you to stare into the dull shine of the " +Describe(\$target)+ "."); 
EmitIn(\$actor."base:environment", Describe(\$actor)+ " gazes into the " +Describe(\$target) + " for several minutes, " +\$actor."trait:possessive" + " eyes slightly glaze over before " +\$actor."trait:possessive" + " snaps out of it.", \$actor); 
return FALSE; 
\}

         X[M] if(NRefDetail(\$target) != "crystal") \{return TRUE;\}

else if(NRefDetail(\$target) == "crystal")\{

if (\$this.lighto == "on") \{ 
  
Set(\$this, "details:default:luminosity", 0.00); 
\$this.lighto = "off"; 
EmitIn(\$actor."base:environment", "The bright glow radiating from the fire within " +Describe(\$target) + " fades quickly."); 
return FALSE; 
\} else \{

Set(\$this, "details:default:luminosity", 100.0); 
\$this.lighto = "on"; 
EmitIn(\$actor."base:environment", Describe(\$target) + " begins to glow brightly with an inner fire, filling the surrounding area with light."); 
return FALSE; 
\}
\}

         X[M] \$item = NRefDetail(\$target);  
 if   (\$item != "kanji") \{return TRUE;\}    
if (\$item == "kanji") \{ 
EmitTo(\$actor, "As you stare closely at the kanji scripter that runs down the length of the katana blade you realize you have absolutely no idea what they say."); 
 
EmitIn(\$actor."base:environment", Describe(\$actor) + " stares closely at the kanji scripter that runs down the length of the katana blade and a look of bewilderment slowly surfaces as its obvious that " + Describe(\$actor)+ " has no idea what they say.", \$actor); 
return FALSE; 
\}
