         X[M] /* spawn the poison vial */
object *collectors;
int i;

collectors = Arr(\$this."trait:collected");
for (i = 0; i \< sizeof(collectors); i++) \{
  if(collectors[i] == \$actor) \{
    EmitTo(\$actor, "You can only have one vial of that filthy black stuff. Save some death for others!");
    return FALSE;
  \}
\}

EmitTo(\$actor, "You cautiously collect a small vial of black substance from the fangs of the ghost spider matriarch.");
EmitIn(\$actor."base:environment", Describe(\$actor)+" cautiously collects a small vials of black substance from the fangs of the ghost spider matriarch.", \$actor);
   \$feather = Spawn(\$\{Allegory:Props:Poisons:B:BlackWidowArachnid\});
   \$feather."base:environment" = \$actor;

\$this."trait:collected" = (Arr(\$this."trait:collected") - (\{ \$actor \})) + (\{ \$actor \});

return FALSE;

