Core:Properties.Core:Property:
         X[M] /* This script runs after the chatter has been teleported to their room and a dream has been chosen */

/* Immobilize chatter */
\$actor."base:immobile" = TRUE;

EmitTo(\$actor, TAG("Waking from sleep, your limbs feel very heavy, as if your mind is awake but your body is not.", "imp"));

\$delay(5, FALSE, "acb1");

EmitTo(\$actor, TAG("You can't move...", "imp"));

\$delay(3, FALSE, "c2c8");

EmitTo(\$actor, TAG("Everything seems normal enough... real enough, right?", "imp"));

\$delay(3, FALSE, "f157");

EmitTo(\$actor, TAG("But...", "imp"));

\$delay(3, FALSE, "e94f");

EmitTo(\$actor, TAG("The walls begin pressing in on all sides...closer....closer....but you still cannot move!", "imp"));

\$delay(5, FALSE, "ff32");

EmitTo(\$actor, TAG("You feel like you can't breathe because of how close they are. Oppressive and heavy grows the air around you as the room becomes completely dark.", "imp"));

\$delay(5, FALSE, "8e6c");

EmitTo(\$actor, TAG("You still can't move!", "imp"));

\$delay(3, FALSE, "1709");

EmitTo(\$actor, TAG("A sinking feeling pulls at your gut as the sensation of falling overwhelms you.", "imp"));

\$delay(5, FALSE, "e708");

EmitTo(\$actor, TAG("You still can't breath...panic wells in your chest...", "imp"));

EmitTo(\$actor, TAG("You gasp desperately.", "social:first"));

\$delay(3, FALSE, "3b9f");

EmitTo(\$actor, TAG("You plummet through the darkness, the sensation of freefall sending shivers of dread up you spine.", "imp"));

\$delay(5, FALSE, "d67c");

EmitTo(\$actor, TAG("You feel your body begin to slow, as though falling through a thick syrup of darkness.", "imp"));

\$delay(5, FALSE, "7c45");

EmitTo(\$actor, TAG("Finally, all is still...", "imp"));

\$delay(2, FALSE, "5854");

/* Have to mobilize the chatter before we can teleport them */
\$actor."base:immobile" = FALSE;

/* Spawn a unique dream room and get it set up */
\$dreamRoom = Spawn(\$\{Ironclaw:Dreams:Halloween:Fire:room\});
Set(\$dreamRoom, "details:door:exit:closed", TRUE);
Set(\$\{Ironclaw:Dreams:Lib\}, Str(\$actor), \$dreamRoom);

/* This delay is necessary so the chatter does not witness the door closing */
\$delay(1, FALSE, "75e7");

/* Teleport chatter to room and place them on the bed */
Act(\$actor, "teleport", \$dest: \$dreamRoom);

/* Immobilize them again */
\$actor."base:immobile" = TRUE;

\$delay(15, FALSE, "7870");

EmitTo(\$actor, TAG("You gasp breathlessly.", "social:first"));

\$delay(15, FALSE, "1a7f");

EmitTo(\$actor, TAG("You scan the hallway desperately.", "social:first"));

\$delay(10, FALSE, "6070");

EmitTo(\$actor, TAG("You hear dull sounds from the other side of the walls. Screaming. Pounding. Shouting. Footsteps overhead. Your heart begins to race.", "imp"));

\$delay(35, FALSE, "7dc8");

EmitTo(\$actor, TAG("You start to walk toward the candle, your heart begins to race even faster. Pounding in your chest.", "imp"));

\$delay(5, FALSE, "4c12");

EmitTo(\$actor, TAG("The sounds grow louder. Running. More screaming. Children crying. Run! Faster!", "imp"));

\$delay(5, FALSE, "31a6");

EmitTo(\$actor, TAG("You take off sprinting, the sound of your feet pounding as loud as your chest. Smoke scents the air. You must get to the candle. It remains far off in the distance. ", "imp"));

\$delay(5, FALSE, "c46a");

EmitTo(\$actor, "The ceiling overhead creaks and groans.");

\$delay(10, FALSE, "ad61");

EmitTo(\$actor, TAG("A feeling of dread washes over you. They won't forget you. They wouldn't.", "imp"));

\$delay(10, FALSE, "aa7f");

EmitTo(\$actor, TAG("....would they?", "imp"));

\$delay(2, FALSE, "5a19");

Set(\$dreamRoom, "details:door:exit:closed", FALSE);

\$delay(10, FALSE, "2c4a");

EmitTo(\$actor, TAG("The sounds gradually disappear. It's silent for a moment. Smoke continues to fill the hallway, making it near impossible to see in the already dark space. You run faster.", "imp"));

\$delay(1, FALSE, "4bab");

EmitTo(\$actor, "The door unlocks.");

\$delay(10, FALSE, "299e");

EmitTo(\$actor, TAG("You run as fast as you can, panic overwhelming you as you stumble and try to regain your footing. You have to get to the door.", "imp"));

\$delay(10, FALSE, "2f0a");

EmitTo(\$actor, TAG("\\"Where's the servant girl, Clarice?!\\", you hear the voices, dim and distant against a low roaring sound from above. The room begins to get hot. Unbearably hot.", "imp"));

\$delay(10, FALSE, "35e6");

EmitTo(\$actor, TAG("The floor begins to quake and buckle, the walls bowing in strange undulations as the room begins to pulsate with glowing orange. You can't breathe. The smoke is choking. Run! Faster!!", "imp"));

\$delay(10, FALSE, "78d3");

EmitTo(\$actor, TAG("The room creaks. The walls groan.", "imp"));

\$delay(5, FALSE, "22d0");

EmitTo(\$actor, TAG("Finally, you reach for the candle. You have to get through the tunnel. To get outside. To get away from the fire.", "imp"));

\$delay(5, FALSE, "6efb");

EmitTo(\$actor, TAG("The candle extinguishes as you pick it up.", "imp"));

\$delay(5, FALSE, "87f8");

EmitTo(\$actor, TAG("The floor gives way underneath you with a sudden crash that drowns out the sound of your screams.", "imp"));

\$delay(5, FALSE, "2abe");

EmitTo(\$actor, TAG("You are falling!", "imp"));

\$delay(5, FALSE, "e0ed");

EmitTo(\$actor, TAG("Above you it is ablaze in orange and fiery red... you brace yourself for impact.", "imp"));

\$delay(5, FALSE, "c3d6");

/* Mobilize them and send them back */
\$actor."base:immobile" = FALSE;
Act(\$actor, "teleport", \$dest: \$actor."virtualhome:home");

/* Spawn the prize */
\$candle = Spawn(\$\{Ironclaw:Dreams:Halloween:Fire:candle\});
\$candle."base:environment" = \$actor;

\$delay(5, FALSE, "716f");

EmitTo(\$actor, TAG("Your eyes snap open. You're covered in sweat and your heart is pounding. It must have been a dream...yes...a terrible dream...", "imp"));

\$delay(5, FALSE, "048a");

EmitTo(\$actor, TAG("Except...", "imp"));

\$delay(5, FALSE, "efa1");

EmitTo(\$actor, TAG("What is that in your hand?", "imp"));

return TRUE;
