Core:Properties.Core:Property:
         X[M] /* This script runs after the chatter has been teleported to their room and a dream has been chosen */

/* Immobilize chatter */
\$actor."base:immobile" = TRUE;

EmitTo(\$actor, TAG("Waking from sleep, your limbs feel very heavy, as if your mind is awake but your body is not.", "imp"));

\$delay(5, FALSE, "acb1");

EmitTo(\$actor, TAG("You can't move...", "imp"));

\$delay(3, FALSE, "c2c8");

EmitTo(\$actor, TAG("Everything seems normal enough... real enough, right?", "imp"));

\$delay(3, FALSE, "f157");

EmitTo(\$actor, TAG("But...", "imp"));

\$delay(3, FALSE, "e94f");

EmitTo(\$actor, TAG("The colors in the room start to fade before your eyes...", "imp"));

\$delay(5, FALSE, "ff32");

EmitTo(\$actor, TAG("Walls bend and distort in impossible ways threatening to collapse on top of you.", "imp"));

\$delay(5, FALSE, "8e6c");

EmitTo(\$actor, TAG("You still can't move!", "imp"));

\$delay(3, FALSE, "1709");

EmitTo(\$actor, TAG("Darkness gathers at the four corners, sucking the light and warmth from the room.", "imp"));

\$delay(5, FALSE, "e708");

EmitTo(\$actor, TAG("It's cold. So so cold.", "imp"));
EmitTo(\$actor, TAG("You shiver uncontrollably.", "social:first"));

\$delay(3, FALSE, "3b9f");

EmitTo(\$actor, TAG("Shadowy tentacles burst from the corners toward you, enveloping you completely!", "imp"));

\$delay(5, FALSE, "d67c");

EmitTo(\$actor, TAG("They pull and yank at your body... cold, dark, suffocating. Are you falling?", "imp"));

\$delay(5, FALSE, "7c45");

EmitTo(\$actor, TAG("Finally, all is still and...", "imp"));

\$delay(2, FALSE, "5854");

/* Have to mobilize the chatter before we can teleport them */
\$actor."base:immobile" = FALSE;

/* Spawn a unique dream room and get it set up */
\$dreamRoom = Spawn(\$\{Ironclaw:Dreams:Halloween:Murder:room\});
Set(\$dreamRoom, "details:door:exit:closed", TRUE);
Set(\$dreamRoom, "details:figure:hidden", TRUE);
Set(\$\{Ironclaw:Dreams:Lib\}, Str(\$actor), \$dreamRoom);

/* This delay is necessary so the chatter does not witness the door closing */
\$delay(1, FALSE, "75e7");

/* Teleport chatter to room and place them on the bed */
Act(\$actor, "teleport", \$dest: \$dreamRoom);
\$actor."base:proximity" = NewNRef(\$dreamRoom, "bed");
\$actor."base:stance" = 3;          /* Lying */
\$actor."base:preposition" = 8;     /* On */

/* Immobilize them again */
\$actor."base:immobile" = TRUE;

\$delay(20, FALSE, "7870");

EmitTo(\$actor, TAG("You shiver uncontrollably.", "social:first"));

\$delay(20, FALSE, "1a7f");

EmitTo(\$actor, TAG("You shiver uncontrollably.", "social:first"));

\$delay(10, FALSE, "6070");

EmitTo(\$actor, TAG("Lightning flashes through the partially boarded windows accompanied by a sharp crack of thunder. Howls of wind beat against the exterior, causing the walls to tremble.", "imp"));

\$delay(35, FALSE, "7dc8");

EmitTo(\$actor, TAG("There is a disturbance outside. Maybe the wind, maybe something else.", "imp"));

\$delay(5, FALSE, "4c12");

EmitTo(\$actor, TAG("You don't hear it now, so probably just the wind.", "imp"));

\$delay(5, FALSE, "31a6");

EmitTo(\$actor, TAG("Whew, yeah... just the wind.", "imp"));

\$delay(5, FALSE, "c46a");

EmitTo(\$actor, "There is a knock from the wooden door.");

\$delay(10, FALSE, "ad61");

EmitTo(\$actor, TAG("A feeling of dread washes over you again. You knew this day was coming.", "imp"));

\$delay(10, FALSE, "aa7f");

EmitTo(\$actor, TAG("Suddenly, the door flies open, nearly ripping itself from the hinges. Gale force winds follow, whipping papers, dust, and debris around in wild disarray.", "imp"));

\$delay(2, FALSE, "5a19");

Set(\$dreamRoom, "details:door:exit:closed", FALSE);

\$delay(10, FALSE, "2c4a");

EmitTo(\$actor, TAG("In the doorway, the silhouette of a figure emerges, its glowing red eyes cutting through the darkness with unmistakable malice.", "imp"));

\$delay(1, FALSE, "4bab");

EmitTo(\$actor, "A dark figure arrives from the wooden door.");
Set(\$dreamRoom, "details:figure:hidden", FALSE);

\$delay(10, FALSE, "299e");

EmitTo(\$actor, TAG("It is time. Nowhere to run. Nowhere to hide. The figure draws closer.", "imp"));

\$delay(1, FALSE, "864c");

EmitTo(\$actor, "You allow a dark figure to stalk " + Describe(\$actor) + ".");
EmitTo(\$actor, "A dark figure approaches " + Describe(\$actor) + ".");
EmitTo(\$actor, TAG("A dark figure stalks to you.", "social:second"));

\$delay(10, FALSE, "b113");

EmitTo(\$actor, TAG("It stares down at you... featureless... hand raised with a glint of something held within.", "imp"));

\$delay(3, FALSE, "2f0a");

EmitTo(\$actor, TAG("A dark figure hauntingly intones to you, \\"Any last words, Mister Greene?\\"", "social:second"));

\$delay(5, FALSE, "35e6");

EmitTo(\$actor, TAG("No. There are no words. Only thoughts of regret and shame. And her face. That beautiful face...", "imp"));

\$delay(5, FALSE, "78d3");

EmitTo(\$actor, "A dark figure readies itself for combat against you.");

\$delay(3, FALSE, "22d0");

EmitTo(\$actor, TAG("A dark figure thrusts its hand at your stomach.", "social:second"));

\$delay(2, FALSE, "6efb");

EmitTo(\$actor, TAG("A sharp cold pain in your stomach jars you awake as you find yourself once again in familiar surroundings.", "imp"));

\$delay(1, FALSE, "e0ed");

/* Mobilize them and send them back */
\$actor."base:immobile" = FALSE;
Act(\$actor, "teleport", \$dest: \$actor."virtualhome:home");

/* Spawn the prize */
\$shiv = Spawn(\$\{Ironclaw:Dreams:Halloween:Murder:shiv\});
\$shiv."base:environment" = \$actor;

\$delay(10, FALSE, "c3d6");

EmitTo(\$actor, TAG("Relief! It was just a dream... just a dream...", "imp"));

\$delay(5, FALSE, "048a");

EmitTo(\$actor, TAG("Except...", "imp"));

\$delay(5, FALSE, "efa1");

EmitTo(\$actor, TAG("What is that in your hand?", "imp"));

return TRUE;
