Core:Properties.Core:Property:
         X[M] /* This script runs after the chatter has been teleported to their room and a dream has been chosen */

/* Immobilize chatter */
\$actor."base:immobile" = TRUE;

EmitTo(\$actor, TAG("You wake in a cloak of darkness...", "imp"));

\$delay(5, FALSE, "ac78");

EmitTo(\$actor, TAG("A feeling of dread washes over you.", "imp"));

\$delay(5, FALSE, "fa26");

EmitTo(\$actor, TAG("You feel as though you aren't alone...", "imp"));

\$delay(5, FALSE, "364c");

EmitTo(\$actor, TAG("The hair on the back of your neck rises as something unseen creeps closer...", "imp"));

\$delay(5, FALSE, "0a65");

EmitTo(\$actor, TAG("Suddenly, something grabs your ankle!", "imp"));

\$delay(5, FALSE, "45e2");

EmitTo(\$actor, TAG("Downward you are yanked through an impossibly small crack in the floor.", "imp"));

\$delay(5, FALSE, "9d4a");

EmitTo(\$actor, TAG("Tumbling and disoriented, you fall...", "imp"));

\$delay(5, FALSE, "07de");

EmitTo(\$actor, TAG("And fall... until...", "imp"));

/* Have to mobilize the chatter before we can teleport them */
\$actor."base:immobile" = FALSE;

/* Spawn a unique dream room and get it set up */
\$dreamRoom = Spawn(\$\{Ironclaw:Dreams:Halloween:Fire:room\});
Set(\$dreamRoom, "details:door:exit:closed", TRUE);

/* This delay is necessary so the chatter does not witness the door closing */
\$delay(1, FALSE, "75e7");

/* Teleport chatter to room and place them on the bed */
Act(\$actor, "teleport", \$dest: \$dreamRoom);

/* Immobilize them again */
\$actor."base:immobile" = TRUE;

\$delay(10, FALSE, "af54");

EmitTo(\$actor, TAG("Sparks of flames shoot up and threaten to burn. The low rumble of foreign chanting floods your ears.", "imp"));

\$delay(30, FALSE, "bcca");

EmitTo(\$actor, TAG("\\"Inyala... Inyala...\\"", "imp"));

\$delay(10, FALSE, "6e09");

EmitTo(\$actor, TAG("Unseen figures push you to your knees, closer yet to the hellish blaze before you.", "imp"));

\$delay(10, FALSE, "5565");

EmitTo(\$actor, TAG("\\"Inyala... Inyala...\\"", "imp"));

\$delay(10, FALSE, "fca3");

EmitTo(\$actor, TAG("You try to run, but you can't! Your hands and feet have been bound. You begin to plead for your life...", "imp"));

\$delay(10, FALSE, "185a");

EmitTo(\$actor, TAG("\\"INYALA... INYALA!\\" A shadowy figure draws closer....", "imp"));

\$delay(10, FALSE, "6a46");

EmitTo(\$actor, TAG("Cruel voices grow louder and louder, drowning out the sounds of your own sobbing...", "imp"));

\$delay(10, FALSE, "115b");

EmitTo(\$actor, TAG("CRACK!", "imp"));

\$delay(5, FALSE, "0afb");

EmitTo(\$actor, TAG("Suddenly you feel a sharp pain in the back of your head, you see red...", "imp"));

\$delay(5, FALSE, "7b5d");

/* Mobilize them and send them back */
\$actor."base:immobile" = FALSE;
Act(\$actor, "teleport", \$dest: \$actor."virtualhome:home");

/* Spawn the prize */
/*\$candle = Spawn(\$\{Ironclaw:Dreams:Halloween:Fire:candle\}); */
\$candle."base:environment" = \$actor;

EmitTo(\$actor, TAG("You slump forward, the hard ground your pillow. Inyala... The last thing you remember before your eyes close. Sleep...", "imp"));
