         X[M] /* D=Does Checks and Writes on Scroll

Check to see if addressee is valid
Check to see if object is in your possession, again.

Write on scroll.
*/

/* fix to stop crash when the addressee is invalid */
\$actor = Obj(\$actor);

if (\$this."base:environment" != \$actor) \{
  Popup(\$actor, \$this, "popup", \$actor: \$actor, \$this: \$this, \$id: \$id, \$guild: \$guild, \$title: \$title, \$body: \$body, \$msg: "You cannot write on an object that is not in your possession!");
  return FALSE;
\}

if (strlen(\$body) \> 10000) \{
  Popup(\$actor, \$this, "popup", \$actor: \$actor, \$this: \$this, \$id: \$id, \$guild: \$guild, \$title: \$title, \$crest: \$crest, \$body: \$body, \$msg: "You may not write more than 10,000 characters on a single page. You are at: "+strlen(\$body));
  return FALSE;
\}

\$body = replace_strings(\$body, "\\r\\n", "\\n", "\", "[b]", "\", "[/b]", "\", "[i]", "\", "[/i]", "\", "[tt]", "\", "[/tt]", "\", "[big]", "\", "[/big]", "\", "[small]", "\", "[/small]", "\", "[blockquote]", "\", "[/blockquote]", "\", "[center]", "\", "[/center]", "\", "[em]", "\", "[/em]", "\", "[strong]", "\", "[/strong]", "\", "[dfn]", "\", "[/dfn]", "\", "[code]", "\", "[/code]");

\$body = replace_strings(\$body, "\", "[samp]", "\", "[/samp]", "\", "[kbd]", "\", "[/kbd]", "\", "[var]", "\", "[/var]", "\", "[cite]", "\", "[/cite]", "\", "[dl]", "\", "[/dl]", "\", "[dt]", "\", "[/dt]", "\", "[dd]", "\", "[/dd]", "\", "[del]", "\", "[/del]", "\
", "[h1]", "\", "[/h1]", "\
", "[h2]", "\", "[/h2]", "\
", "[h3]", "\", "[/h3]", "\
", "[h4]", "\", "[/h4]", "\
", "[h5]", "\", "[/h5]", "\
", "[h6]", "\", "[/h6]");

\$body = replace_strings(\$body, "\", "[ol]", "\", "[/ol]", "\
", "[li]", "\", "[/li]", "\", "[pre]", "\", "[/pre]", "\", "[q]", "\", "[/q]", "\", "[sub]", "\", "[/sub]", "\", "[sup]", "\", "[/sup]", "\", "[u]", "\", "[/u]", "\", "[ul]", "\", "[/ul]", "\\'", "[quote]");

\$body = replace_xml(\$body);

\$body = replace_strings(\$body, "[b]", "\", "[/b]", "\", "[i]", "\", "[/i]", "\", "[tt]", "\", "[/tt]", "\", "[big]", "\", "[/big]", "\", "[small]", "\", "[/small]", "\", "[blockquote]", "\", "[/blockquote]", "\", "[center]", "\", "[/center]", "\", "[em]", "\", "[/em]", "\", "[strong]", "\", "[/strong]", "\", "[dfn]", "\", "[/dfn]", "\", "[code]", "\", "[/code]", "\");

\$body = replace_strings(\$body, "[samp]", "\", "[/samp]", "\", "[kbd]", "\", "[/kbd]", "\", "[var]", "\", "[/var]", "\", "[cite]", "\", "[/cite]", "\", "[dt]", "\", "[/dt]", "\", "[dd]", "\", "[/dd]", "\", "[dl]", "\", "[/dl]", "\", "[del]", "\", "[/del]", "\", "[h1]", "\
", "[/h1]", "\", "[h2]", "\
