Core:PCProperties.Core:Property:
         X[M] \$object = Spawn(\$\{Ironclaw:Staff:Cersei:Coder:Cloud\}); \$object."base:environment" = \$actor."base:environment";                  

/* emit message to player and room */                  
EmitTo(\$actor, "Swishing your hand, you produce from your fingertips wisps of condensed air that form "+Describe(\$object, nil, nil, STYLE_NONPOSS)+".");                  
EmitIn(\$actor."base:environment", Describe(\$actor)+" swishes " + \$actor."base:possessive" + " hand, producing from " + \$actor."base:possessive" + " fingertips wisps of condensed air that form "+Describe(\$object, nil, nil, STYLE_NONPOSS)+".", \$actor);    
return FALSE;
