         X[M] /*
** \$month
** \$day
*/
string month;
int i;

\$db = \$\{Ironclaw:Data:Events\};
\$counter = 0;

month = ::get_month_name(\$month: \$month);

if(!Get(\$db, "event:" + lower_case(month))) \{
    return FALSE;
\}

if(!Get(\$db, "event:" + lower_case(month))[Str(\$day)]) \{
    return FALSE;
\}

\$listing = Get(\$db, "event:" + lower_case(month))[Str(\$day)];

if(!map_sizeof(\$listing)) \{
    return FALSE;
\}

\$times = map_indices(\$listing);

for(i = 0; i \< sizeof(\$times); i++) \{
    if(sizeof(\$listing[\$times[i]])) \{
        \$counter ++;
    \}
\}

return \$counter;

         X[M] mapping holidaysDB, holidayMap;
mixed* holidaysAll, days, match;
string month;
int i;

holidaysDB = Get(\$\{Ironclaw:Lib:Builders:Time\}, "time_holidays");

holidaysAll = map_indices(holidaysDB);

match = (\{ \});
for(i = 0; i \< sizeof(holidaysAll); i++) \{
    holidayMap = holidaysDB[holidaysAll[i]];
    month = holidayMap["month"];
    days = holidayMap["days"];

    if(member(Int(\$day), days) \&\& \$month == month) \{
        match += (\{ holidaysAll[i] \});
    \}
\}

return match;

         X[M] string month, icomonth, output;
mapping monthlyListing, listing;
mixed *times, *entry, *entries;
int i, j;

\$eventDB = \$\{Ironclaw:Data:Events\};

\$access = (\{ \});
if(\$eventDB.access) \{
    \$access = \$eventDB.access;
\}

if(!sscanf(\$month + " " + \$day + " " + \$year, "%d %d %d", \$month, \$day, \$year)) \{
    return "Invalid Input!";
\}

\$other = ([ ]);

if(\$icoday1 == "Dimache" \&\& \$dayselector == "8") \{
    \$other += ([ "0000":(\{ (\{ "Holy Day", "A Holy Day for those who follow S'Allumer. Trade and dueling are restricted.", "--", \$year, "H" \}) \}) ]);
\} else if(\$icoday1 == "Dimache" \&\& \$dayselector == "16") \{
    \$other += ([ "0000":(\{ (\{ "Holy Day", "A Holy Day for those who follow S'Allumer. Trade and dueling are restricted.", "--", \$year, "H" \}) \}) ]);
\} else if(\$icoday2 == "Dimache" \&\& \$dayselector == "8") \{
    \$other += ([ "0800":(\{ (\{ "Holy Day", "A Holy Day for those who follow S'Allumer. Trade and dueling are restricted.", "--", \$year, "H" \}) \}) ]);
\} else if(\$icoday2 == "Dimache" \&\& \$dayselector == "16") \{
    \$other += ([ "1600":(\{ (\{ "Holy Day", "A Holy Day for those who follow S'Allumer. Trade and dueling are restricted.", "--", \$year, "H" \}) \}) ]);
\}


\$weekdays = (\{ "Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday" \});
\$weekday1 = \$weekdays[Int(\$weekday)-1];

month = ::get_month_name(\$month: \$month);
icomonth = replace_strings(month[0..2], "Jan", "Yule", "Feb", "Snow", "Mar", "Sap", "Apr", "Grass", "May", "Flower", "Jun", "Strawberry", "Jul", "Thunder", "Aug", "Green", "Sep", "Harvest", "Oct", "Hunter's", "Nov", "Frost", "Dec", "Night" );

output = "";
output += "\";

output += "\" + "\<\< Go back" +
          "\\$[\\"\\";]" +
          "\" +
          "\" +
          "\";

if(\$result) \{
    output += "\\" + \$result + "\\";
\}

output += "\
\";
output += "\
";
output += "  \";
output += "    \\\Events for the " + Str(desc_ordinal(Int(\$day))) + " day of " + Str(icomonth) + ", year " + Str(\$year - 1119) + "\\\";
output += "  \";
output += "\";
output += "\
";
output += "  \";
output += "    \\\(" + \$weekday1 + " " + month + " " + Str(\$day) + ", " + Str(\$year) + ")\\\";
output += "  \";
output += "\";
output += "\\
";

\$holidays = ::query_holidays(\$month: icomonth, \$day: \$day);

for(i = 0; i \< sizeof(\$holidays); i++) \{
    \$hname = Str(Get(\$\{Ironclaw:Lib:Builders:Time\}, "time_holidays")[\$holidays[i]]["name"]);
    \$hdesc = Str(Get(\$\{Ironclaw:Lib:Builders:Time\}, "time_holidays")[\$holidays[i]]["description"]);

    if(\$other["0000"]) \{
        \$other["0000"] += (\{ (\{ \$hname + " (Holiday)", capitalize(\$hdesc), "--", \$year, "H" \}) \});
    \} else \{
        \$other += ([ "0000":(\{ (\{ \$hname + " (Holiday)", capitalize(\$hdesc), "--", \$year, "H" \}) \}) ]);
    \}
\}

monthlyListing = ([ ]);
if(Get(\$eventDB, "event:" + Str(lower_case(month)))) \{
    monthlyListing = copy(Get(\$eventDB, "event:" + Str(lower_case(month))));
\}

listing = ([ ]);
if(!map_sizeof(monthlyListing) \&\& !map_sizeof(\$other)) \{
    output += "\No scheduled events.\
\";
\} else \{
    if(!monthlyListing[Str(\$day)] \&\& !map_sizeof(\$other)) \{
        output += "\No scheduled events.\
\";
    \} else if(monthlyListing[Str(\$day)])\{
        if(!map_sizeof(monthlyListing[Str(\$day)])) \{
            monthlyListing[Str(\$day)] = nil;
            output += "\No scheduled events.\
\";
        \} else \{
            listing = monthlyListing[Str(\$day)];
        \}
    \}
\}

\$othertimes = (\{ \});
if(map_sizeof(\$other)) \{
    \$othertimes = map_indices(\$other);
\}

for(i = 0; i \< sizeof(\$othertimes); i++) \{
    if(listing[\$othertimes[i]]) \{
        listing[\$othertimes[i]] += copy(\$other[\$othertimes[i]]);
    \} else \{
        listing[\$othertimes[i]] = copy(\$other[\$othertimes[i]]);
    \}
\}

times = (\{ \});
if(map_sizeof(listing)) \{
    times = map_indices(listing);
\}

if(sizeof(times)) \{
    for(i = 0; i \< sizeof(times); i++) \{
        \$t = times[i];
        \$t1 = \$t[0..1];
        \$t2 = \$t[2..3];

        \$icoday = \$icoday1;
        if(Int(\$t1) \>= Int(\$dayselector)) \{
            \$icoday = \$icoday2;
        \}

        \$ampm = "am";
        if(Int(\$t1) \>= 12 \&\& Int(\$t1) \< 24) \{
            \$ampm = "pm";
        \}

        entries = listing[times[i]] -= (\{ nil \});


        for(j = 0; j \< sizeof(entries); j++) \{
          entry = entries[j];
          if(sizeof(entry) \>= 4) \{

            if(\$color=="#ffffff") \{
                \$color = "#ffffee";
            \} else \{
                \$color = "#ffffff";
            \}

            if(entry[0] == "Holy Day") \{
                \$color = "#ffff99";
            \}

            output += "\";
            output += "\
";
            output += "  \";
            output += "    \\" + entry[0] + "\\\\";

    if(\$actor \&\& \$actor != "" \&\& Obj(\$actor) \&\& member(Obj(\$actor), \$access) \&\& sizeof(entry) == 4) \{

            output += "    \" + "(edit)" +
                      "      \\$[\\"\\";]" +
          	      "       \" +
                      "      \" +
                      "    \ \ ";
            output += "    \" + "(delete)" +
                      "      \\$[\\"\\"; Obj(\\"" + Str(\$eventDB) + "\\").\\"event:" + lower_case(::get_month_name(\$month: Int(\$month))) + "\\"[\\"" + Str(\$day) + "\\"][\\"" + Str(\$t1) + Str(\$t2) + "\\"][Int(\\"" + Str(j) + "\\")] = nil;]" +
          	      "       \" +
                      "      \" +
                      "    \";
     \}
            output += "  \";
            output += "\";
            output += "\
";
            output += "  \";
            output += "    \";
            output += "  \";
            output += "\";
            output += "\
";
            output += "  \";
            output += "    \\\ \ Description \\";
            output += "  \";
            output += "  \";
            output += "    \" + entry[1] + "\";
            output += "  \";
            output += "\";
            output += "\
";
            output += "  \";
            output += "    \\\ \ Date \\";
            output += "  \";
            output += "  \";
            output += "    \" + \$icoday + ", the " + Str(desc_ordinal(Int(\$day))) + " day of " + Str(icomonth) + ", year " + Str(\$year - 1119) + "\";
            output += "  \";
            output += "\";
            output += "\
";
            output += "  \";
            output += "    \\\ \ Time \\";
            output += "  \";
            output += "  \";
            output += "    \" + \$t1 + ":" + \$t2 + \$ampm + " Skotos Time\";
            output += "  \";
            output += "\";
            output += "\
";
            output += "  \";
            output += "    \\\ \ Location \\";
            output += "  \";
            output += "  \";
            output += "    \" + entry[2] + "\";
            output += "  \";
            output += "\";
            output += "\
";
            output += "  \";
            output += "    \";
            output += "  \";
            output += "\";

            output += "\\
";
          \} else \{
            output += "\Error displaying event.\";
          \}
        \}
        if(!sizeof(entries)) \{
          monthlyListing[Str(\$day)][Str(\$t1) + Str(\$t2)] = nil;
          Set(\$eventDB, "event:" + lower_case(Str(month)), monthlyListing);
        \}
    \}
\}

output += "\\\\
\\" + "(add event)" +
          "\\$[\\"\\";]" +
          "\" +
          "\" +
          "\\\\\";



output += "\";

return ParseXML(output);
