         X[M] /* give a curse to the player */

if(\$actor.cursed) return TRUE;
\$actor.cursed = TRUE;

\$delay(5, TRUE, "8d58");

if(\$curselevel \>= 7) \{
  /* hiccups and vomit and convusions */
  \$actor.cursed = time() + 60*60*\$curselevel*3;
  while(\$actor.cursed \&\& time() \< \$actor.cursed) \{
    \$tmp = random(70);
    \$tmp = 60;
    if(\$tmp \< 1) \{
      Social(\$actor, "spin", "unnaturally", nil, nil, NewNRef(\$actor, "head"));
    \} else if(\$tmp \< 5) \{
      Social(\$actor, "vomit", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally", "messily"), nil, "on", \$actor);
    \} else if(\$tmp \< 20) \{
      Social(\$actor, "convulse", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally", "suddenly"));
    \} else if(\$tmp \< 35) \{
      Social(\$actor, "hiccup", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally"));
    \} else \{
      EmitTo(\$actor, TAG(oneof(
"You wonder why the people who anger you are still alive.",
"You find yourself pondering the benefits of pain.",
"You wonder why the church of S'allumer is so popular, considering what hypocrites they are.",
"You find yourself craving the taste of flesh.",
"You realize that nothing in the rules of S'allumer say you have to be nice.",
"You begin to think that undead may make better companions than the living.",
"You think that you should probably be wearing only black.",
"You decide to decorate your room in black.",
"You know, the next person who insults you should probably get a fist in their face.",
"You know, people talk too much!\\nThe next person who talks to you should probably get a fist in their face.",
"Why have you been wasting your life, when the dark arts are such a quick path to power, wealth, and glory?",
"It seems so pointless, when there's always more people with weapons than there are healers. Why not just let injured people suffer?"
), "IMP"));
    \}
    \$delay(random(60*10), TRUE, "2046");
  \}
\} else if(\$curselevel \>= 5) \{
  /* hiccups and vomit and convusions */
  \$actor.cursed = time() + 60*60*24;
  while(\$actor.cursed \&\& time() \< \$actor.cursed) \{
    \$tmp = random(35);
    if(\$tmp \< 1) \{
      Social(\$actor, "spin", "unnaturally", nil, nil, NewNRef(\$actor, "head"));
    \} else if(\$tmp \< 5) \{
      Social(\$actor, "vomit", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally", "messily"), nil, "on", \$actor);
    \} else if(\$tmp \< 20) \{
      Social(\$actor, "convulse", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally", "suddenly"));
    \} else \{
      Social(\$actor, "hiccup", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally"));
    \}
    \$delay(random(60*10), TRUE, "25ad");
  \}
\} else if(\$curselevel \>= 3) \{
  /* hiccups and vomit */
  \$actor.cursed = time() + 60*60*20;
  while(\$actor.cursed \&\& time() \< \$actor.cursed) \{
    \$tmp = random(20);
    if(\$tmp \< 1) \{
      Social(\$actor, "spin", "unnaturally", nil, nil, NewNRef(\$actor, "head"));
    \} else if(\$tmp \< 5) \{
      Social(\$actor, "vomit", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally", "messily"), nil, "on", \$actor);
    \} else \{
      Social(\$actor, "hiccup", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally"));
    \}
    \$delay(random(60*10), TRUE, "0113");
  \}
\} else if(\$curselevel \> 1) \{
  /* hiccups and vomit */
  \$actor.cursed = time() + 60*60*15;
  while(\$actor.cursed \&\& time() \< \$actor.cursed) \{
    if(!random(5)) \{
      Social(\$actor, "vomit", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally", "messily"), nil, "on", \$actor);
    \} else \{
      Social(\$actor, "hiccup", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally"));
    \}
    \$delay(random(60*10), TRUE, "d8a9");
  \}
\} else \{
  /* hiccups */
  \$actor.cursed = time() + 60*60*5;
  while(\$actor.cursed \&\& time() \< \$actor.cursed) \{
    Social(\$actor, "hiccup", oneof("violently", "convulsively", "spontaneously", "uncontrollably", "unnaturally"));
    \$delay(random(60*10), TRUE, "41b2");
  \}
\}

\$actor.cursed = nil;

return TRUE;

