Core:Properties.Core:Property:
         X[M] /* decided not to play */                                                       
EmitTo(\$actor, "You choose not to bet on the turtle races.");                                                       
return TRUE;

         X[M] \$tokens = Match(\$actor, "token", \$actor, "festival");                                                
if(sizeof(\$tokens) \< 1) \{                                                
  EmitTo(\$actor, "You don't have a token. Buy one from the festival attendant.");                                                   
  return TRUE;                                                
\} else \{                                                
  EmitTo(\$actor, "You turn a token over to the ferris wheel attendant.");                                                
  Slay(NRefOb(\$tokens[0]));                                                
\}           
   \$delay(5, TRUE, "030f");                      
/* Give first emit message */                           
EmitIn(\$this."base:environment", "The ferris wheel attendant helps " +Describe(\$actor)+" settle themselves in the bucket seat and closes the security bar across " +\$actor."trait:possessive"+ " lap. Then the attendants slowly start to turn the crank! The ferris wheel " +oneof("begins moving", "starts traveling", "proceeds", "embarks", "takeoffs", "set offs", "commences", "sets forth")+ " on the upward climb. The view of town is " +oneof("minute.", "barely seen.", "far away.", "slightly visible.", "just over the horizon."));      
      
/* Delay for 10 seconds */                      
\$delay(23, TRUE, "4540");     
EmitIn(\$this."base:environment", Describe(\$actor)+ " continues the " +oneof("upward climb", "travel upward", "path to the top", "takeoff to the peak")+ " while the bucket seat " +oneof("slowly rocks", "gently swings", "pitches to and fro")+ ".");    
    
/* Delay for 10 seconds */                    
\$delay(23, TRUE, "9295");    
EmitIn(\$this."base:environment", "The ride finally makes it to the top and the  " +oneof("view of town is amazing.", "entire town is easily seen.", "landscape for miles is breathtaking.", "spectacle of town is picturesque.")+ "  The bucket seat  " +oneof("stall", "pause", "stop", "rest")+ " at the top for a few minutes, just long enough to enjoy the ride.");    
    
/* Delay for 10 seconds */                    
\$delay(23, TRUE, "4824");    
EmitIn(\$this."base:environment", "The ferris wheel attendants turn the crank again and " +Describe(\$actor)+" starts the journey down. The view of town shrinks until the bottom is reached. The festival attendant helps " +\$actor."trait:objective"+ " from the seat and the ride is over.");

         X[M] /* ask if the player wants to play this */                                                                        
interaction::question(\$question: "The view from the top is amazing. It's only one token, what do you say?", \$expires: 80, \$env: "TRUE", \$yes_ob: \$this, \$no_ob: \$this, \$expire_ob: \$this, \$yes_fun: "ride_emits", \$no_fun: "no_ride", \$expire_fun: "expired_ride");                               
return FALSE;
