Core:Properties.Core:Property:
         X[M] \$roll = ironclaw::roll(\$roller: \$actor, \$skills: (\{ \}), \$bonus: (\{ "trait:perception" \}));

EmitTo(\$actor, "You collect a wooden mallet and align with the clay ball in starting position.");
EmitIn(\$actor."base:environment", Describe(\$actor)+" collects the wooden mallet and aligns with the clay ball in the starting position.", \$actor);

switch(\$roll) \{
  case 1:
    EmitTo(\$actor, "You swing the mallet at the clay ball but miss entirely. Correcting your stance, you try again. After all of your strokes are done, you realise you haven't made it through even the first of the hoops. Zero points awarded.");
    EmitIn(\$actor."base:environment", Describe(\$actor) + " swings the mallet at the clay ball but misses entirely. They correct their stance to attempt again. After using up all five strokes, they only manage to get close to but not through the first hoop. No points are awarded.", \$actor);
    break;
  case 2:
    EmitTo(\$actor, "You strike at the clay ball time after time, but you only manage to get it through the first hoop before using up all your strokes. One point awarded.");
    EmitIn(\$actor."base:environment", Describe(\$actor)+" swings the playing mallet at the clay ball and manages to direct " + \$actor."base:possessive" + " ball through the first hoop. One point is awarded.", \$actor);
    break;
  case 3:
    EmitTo(\$actor, "You swing your playing mallet carefully, striking the ball through two of the hoops before using up all your strokes. Two points awarded.");
    EmitIn(\$actor."base:environment", Describe(\$actor)+" swings the playing mallet with as much care as " + \$actor."trait:nominative" + " can and manages to nudge the clay ball through two of the hoops before "+\$actor."trait:nominative"+" has used up all the strokes. Two points are awarded.", \$actor);
    break;
  case 4:
    EmitTo(\$actor, "You drive the ball straight and true, getting the ball through all four hoops with only two strokes. Seven points are awarded.");
    EmitIn(\$actor."base:environment", "Driving the ball straight and true, "+Describe(\$actor)+" drives the ball through all four hoops and " + \$actor."trait:nominative" + " does so with only two strokes. Seven points are awarded.", \$actor);
    break;
  case 5:
    EmitTo(\$actor, "You manage to get the ball through all four hoops before running out of strokes. Four points are awarded.");
    EmitIn(\$actor."base:environment", "Carefully swinging the playing mallet, "+Describe(\$actor)+" manages to get the ball through all four hoops just before running out of strokes. Four points are awarded.", \$actor);
    break;
  case 6:
    EmitTo(\$actor, "You swing the mallet carefully and manage to drive the ball through three of the hoops before running out of strokes. Three points awarded.");
    EmitIn(\$actor."base:environment", Describe(\$actor)+" swings the mallet carefully, allowing " + \$actor."base:objective" + " to drive the ball through three hoops before running out of strokes. Three points are awarded.", \$actor);
    break;
  case 7:
    EmitTo(\$actor, "With concentrated care, you swing the playing mallet and direct the clay ball through all four hoops with two extra strokes to spare. Six points awarded.");
    EmitIn(\$actor."base:environment", Describe(\$actor)+" swings the playing mallet with great care and directs the clay ball through all four hoops with two extra strokes to spare. Six points are awarded.", \$actor);
    break;
  default:
    EmitTo(\$actor, "You swing the playing mallet with ease and send the playing ball through all four hoops with one stroke! Eight points awarded.");
    EmitIn(\$actor."base:environment", Describe(\$actor)+" swings the playing mallet effortlessly and sends the ball through all four hoops with one stroke! Eight points are awarded.", \$actor);
\}

return FALSE;
