Core:Properties.Core:Property:
         X[M] /* check detail for each craft material */ 
if(NRefDetail(\$dob[0]) == "strawyellow") \{ 
/* set properties specific to this detail only */ 
\$color = "yellow"; 
\$cont = 1; 
\} 
if(NRefDetail(\$dob[0]) == "strawgreen") \{ 
\$color = "green"; 
\$cont = 1; 
\} 
if(\$cont) \{ 
/* set properties specific to any of the craft materials from this source */ 
\$objecttype = "straw"; 
/* grab arrays of snames, pnames, and adjectives */ 
\$temp = "details:" +NRefDetail(\$dob[0])+ ":snames"; 
\$sn = Get(\$this, \$temp); 
/* add additional if needed */ 
\$sn += (\{ "stalk", "hay" \}); 
\$temp = "details:" +NRefDetail(\$dob[0])+ ":pnames"; 
\$pn = Get(\$this, \$temp); 
\$temp = "details:" +NRefDetail(\$dob[0])+ ":adjectives"; 
\$adj = Get(\$this, \$temp); 
/* scent and taste */ 
\$scent = "freshly cut hay"; 
\$taste = "like freshly cut hay"; 
Call(\$\{Ironclaw:Staff:Death:Crafting:Unique:CraftLib\}, "get:material", \$actor: \$actor, \$color: \$color, \$objecttype: \$objecttype, \$sn: \$sn, \$pn: \$pn, \$adj: \$adj, \$scent: \$scent, \$taste: \$taste); 
\} 
return TRUE;

         X[M] if(NRefDetail(\$what[0]) == "chalkboard") \{       
EmitIn(\$actor."base:environment", Describe(\$actor) + " snags a piece of chalk from midair and begins to scrawl on " +Describe(\$what[0])+ ".");       
(\$delay, 1);      
Social( \$actor, "+lg" );       
return FALSE;       
\}       
return TRUE;
