Core:Properties.Core:Property:
         X[M] /* Let people approach hanging PCs */    
if(\$targetpartition == "hanging upside-down from a tree") return TRUE;    
return FALSE;

         X[M] EmitTo(\$actor, "A sudden WHOOSH is heard as you step down on a soft patch of ground near the opening of the tents. A few moments later, you find yourself hanging, upside-down, from a tree branch. A sharp knife and some ingenuity will certainly release you from the trap.");   
EmitIn(\$actor."base:environment", "A sudden WHOOSH is heard as " + Describe(\$actor) + " steps down on a soft patch of ground near the opening of the tents. A few moments later, " + Str(\$actor."base:pronoun") + " finds " + Str(\$actor."base:objective") + "self hanging upside-down from a tree branch. A sharp knife and some ingenuity will certainly release " + Describe(\$actor) + " from the trap.", \$actor);   

\$actor."room:partition" = "hanging upside-down from a tree"; /* set the partition so they can't move around anymore */   
/* set them to be hanging from the tree */   
\$actor."base:proximity" = NewNRef(\$\{[Allegory:Props:Plants:P:Plum:PlumPlant]#204690\}, "default");   
\$actor."base:stancestring" = "hanging";   
\$actor."base:prepositionstring" = "on";   
\$actor."merry:inherit:react:release-dob%mountain_snaretrap" = \$this; /* set the script to let them be cut down */   

return FALSE;

         X[M] /* Because this is inherited from the victim, \$this is set to the victim */    
/* Can't release anything if you are caught in a snare */    
if(\$actor."room:partition" == "hanging upside-down from a tree") \{    
    EmitTo(\$actor, "You attempt to release yourself from the trap but only succeed in making yourself tired.");    
    EmitIn(\$actor."base:environment", Describe(\$actor) + " flails " + oneof("pathetically", "uselessly", "pitifully") + " trying to release "+\$actor."base:objective"+"self.", \$actor);    
    return FALSE;    
\}    

/* Did they release anyone from a snare? */    
if(\$this."room:partition" != "hanging upside-down from a tree") return TRUE;    
\$this."room:partition" = nil;    

\$delay(0.1, TRUE, "02d8");    

\$this."merry:inherit:react:release-dob%mountain_snaretrap" = nil;    

/* Did they land fine? */    
\$hit = random(12);    
\$bonus = (\{ "trait:agility" \});    
\$defense = ironclaw::roll(\$roller: \$this, \$skills: (\{ \}), \$bonus: (\{ "trait:agility" \}));    
if(\$defense \>= \$hit) \{    
  EmitTo(\$this, "You land nimbly on your feet.");    
  EmitIn(\$actor."base:environment", Describe(\$this)+" lands nimbly on "+\$this."base:possessive"+" feet.", \$victim);    
  \$this."base:stancestring" = "standing";    
\} else \{    
  \$this."base:stancestring" = "lying";    
  Call(\$\{Ironclaw:Props:Plot:Bandit:WoodlandRoad\}, "injure", \$who: \$this, \$where: "head", \$injury: "injury:fall");    
\}    

return TRUE;

         X[M] EmitTo(\$actor, "A sudden WHOOSH is heard as you step down on a soft patch of ground near the opening of the tents. A few moments later, you find yourself hanging, upside-down, from a tree branch. A sharp knife and some ingenuity will certainly release you from the trap.");    
EmitIn(\$actor."base:environment", "A sudden WHOOSH is heard as " + Describe(\$actor) + " steps down on a soft patch of ground near the opening of the tents. A few moments later, " + Str(\$actor."base:pronoun") + " finds " + Str(\$actor."base:objective") + "self hanging upside-down from a tree branch. Perhaps with a good knife and some  ingenuity a plan come be executed to release " + Describe(\$actor) + " from the trap.", \$actor);    

\$actor."base:stancestring" = "hanging"; /* set them to be hanging */    
\$actor."merry:inherit:react:release-dob%mountain_snaretrap" = \$this; /* set the script to let them be cut down */    
\$actor."room:partition" = "hanging upside-down from a tree"; /* set the partition so they can't move around anymore */    

return FALSE;
