/* Did they land fine? */   
\$hit = random(12);   
\$bonus = (\{ "trait:agility" \});   
\$defense = ironclaw::roll(\$roller: \$this, \$skills: (\{ \}), \$bonus: (\{ "trait:agility" \}));   
if(\$defense \>= \$hit) \{   
  EmitTo(\$this, "You land nimbly on your feet.");   
  EmitIn(\$actor."base:environment", Describe(\$this)+" lands nimbly on "+\$this."base:possessive"+" feet.", \$victim);   
  \$this."base:stancestring" = "standing";   
\} else \{   
  \$this."base:stancestring" = "lying";   
  Call(\$\{Ironclaw:Props:Plot:Bandit:WoodlandRoad\}, "injure", \$who: \$this, \$where: "head", \$injury: "injury:fall");   
\}   

return TRUE;

         X[M] EmitTo(\$actor, "A sudden WHOOSH is heard as you step down on a soft patch of ground near the opening of the tents. A few moments later, you find yourself hanging, upside-down, from a tree branch.");   
EmitIn(\$actor."base:environment", "A sudden WHOOSH is heard as " + Describe(\$actor) + "steps down on a soft patch of ground near the opening of the tents. A few moments later, " + Str(\$actor."base:pronoun") + " finds " + Str(\$actor."base:objective") + "self hanging upside-down from a tree branch.", \$actor);   

\$actor."base:stancestring" = "hanging";   
\$actor."merry:inherit:react:release-dob%mountain_snaretrap" = \$this;   
\$actor."room:partition" = "hanging upside-down from a tree";   

return FALSE;
