              \Recurring\\
\$[/* How often is this shipment sent? */        
  if(\$biditem[5][1] == -1) \{        
    \$output = "a new shipment";        
  \} else if(\$biditem[5][1] == 1) \{        
    \$output = "1 shipment";        
  \} else \{        
    \$output = Str(\$biditem[5][1])+" shipments, one sent";        
  \}        
  if(\$biditem[5][2] == 1) \{        
    \$output += " every day.";        
  \} else if(\$biditem[5][2] != 0) \{        
    \$output += " every "+Str(\$biditem[5][2])+" days";        
  \}        
  return \$output;]
