              \Transport:\\\$(biditem.5.0)\
            \\

              \Highest Bid:\\
\$[/* give bid price, and allow the chance to bid */        
  if(\$biditem[6]) \{        
    if(\$biditem[6] == 1) \{        
      \$output = "1 denar";        
    \} else \{        
      \$output = Str(\$biditem[6])+" denarii";        
    \}        
    if(\$biditem[7] == \$guild) \{        
      \$output += " placed by "+\$guild.fullname+".";        
      \$output = "\"+\$output+"\";        
    \} else \{        
      \$output += " placed by another guild.";        
      \$output = "\"+\$output+"\";        
    \}        
  \} else \{        
    \$output = "no bid";        
  \}        
  return \$output;]
