    /* set puzzle 3 part 3 to set*/
    \$this."trait:puzzlethree" = nil;
    /* set puzzle so that other puzzles can be solved */
    \$this."trait:unfinished" = nil;
    return TRUE;
 
/*if wrong detail is delivered*/
\} else \{
    EmitTo(\$actor, "Nothing reacts to your attempts. Try with something more evidently usable.");
    EmitIn(\$actor."base:environment", Describe(\$actor) + " tries to manipulate the globe. The emphasis is on TRY.", \$actor);
\}
return TRUE;

         X[M] /* PUZZLE 1 PART 2 OF PARTS X*/
/*IF NOTHING PREVENTS THIS FROM BEING PERFORMED LIKE OTHER PUZZLES BEING UNFINISHED*/
if (!\$this."trait:unfinished") \{

/*do the actual action*/
if(sizeof(\$dob) != 1) return TRUE;
\$target = \$dob[0];
if(NRefDetail(\$target) == "snakehead") \{
   if(\$adverb == "counter-clockwise") \{
         EmitTo(\$actor, "You attempt to turn the snake head figurine counter-clockwise but encounter resistance. You decide to give up before it breaks.");
         EmitIn(\$actor."base:environment", Describe(\$actor) + " attempts to turn the snake head figurine counter-clockwise but encounters resistance, thus " +\$actor."trait:nominative"+ " decides to give up before it breaks.", \$actor);
   \} else if(\$adverb == "clockwise") \{
         EmitTo(\$actor, "You turn the snake head figurine clockwise pushing the handle into the globe simultaneously sliding a flat circular mirror from the equator of the globe.");
         EmitIn(\$actor."base:environment", Describe(\$actor) + " turns the snake head figurine clockwise pushing the handle into the globe simultaneously sliding a flat circular mirror from the equator of the globe.", \$actor);
         /*unhide detail mirror*/
          \$this."details:mirror:hidden" = FALSE;
         /*hide detail snakehead*/
         \$this."details:snakehead:hidden" = TRUE;
        /* set puzzle 1 part 2 to set*/
        \$this."trait:puzzleone" = 2;
        /* set puzzle so that other puzzles can not be solved while this is */
        \$this."trait:unfinished" = 1;
/*if adjective not given*/
   \} else \{
     EmitTo(\$actor, "Which way?");
     EmitIn(\$actor."base:environment", Describe(\$actor) + " undecidedly fidgets with the snake head figurine of the globe.", \$actor);
  \}
/*if wrong detail is delivered*/
\} else \{
     EmitTo(\$actor, "You are kind of undecided. What did you want to turn anyway?");
     EmitIn(\$actor."base:environment", Describe(\$actor) + " undecidedly runs " +\$actor."base:possessive"+ " fingers over the surface of the globe.", \$actor);
\}
return TRUE;

/*IF SOMETHING PREVENTS THIS TO BE PERFORMED */
\} else \{ 
     EmitTo(\$actor, "Something in the mechanism of the globe prevents you to do this. Maybe if all other puzzles would be returned into the depth of the globe nothing would disturb this one from being solved.");
\}
return TRUE;

         X[M] /* PUZZLE 5 PART 2 OF PARTS 4*/

/*IF NOTHING PREVENTS THIS FROM BEING PERFORMED LIKE OTHER PUZZLES BEING UNFINISHED*/
if (!\$this."trait:unfinished") \{
   /*do the actual action*/
   if(sizeof(\$dob) != 1) return TRUE;
   \$target = \$dob[0];
   if(NRefDetail(\$target) == "south") \{
     if(\$this."trait:puzzlefive" != 1) \{EmitTo(\$actor, "Something prevents you from performing that task, though you get the sense the direction you took is a good one."); return FALSE; 
     \}
      if(\$adverb == "left") \{
         EmitTo(\$actor, "You try to twist the Southern Hemisphere to the left but only manage a half inch when the clogs and springs tense. With fear you would break the globe you ease the pressure. Maybe the other direction would be better.");
         EmitIn(\$actor."base:environment", Describe(\$actor) + " starts to twist "+\$actor."base:possessive" +" globe to the left causing the clogs and springs to tense within the globe.", \$actor);
      \} else if(\$adverb == "right") \{
         EmitTo(\$actor, "You twist the Southern Hemisphere of the globe to the right smoothly and soundlessly when a little click halts you.");
         EmitIn(\$actor."base:environment", Describe(\$actor) + " smoothly and soundlessly twists " +\$actor."base:possessive"+ " globe to the right.", \$actor);
         /* set puzzle 5 part 2 to set*/
         \$this."trait:puzzlefive" = 2;
      /*if adjective not given*/
      \} else \{
           EmitTo(\$actor, "You should decide which way to twist the hemispheres, the thread within the globe allows you to do it left or right only.");
           EmitIn(\$actor."base:environment", Describe(\$actor) + " circularly shifts the hemispheres checking what movements the mechanism permits.", \$actor);
       \}
       return TRUE;

   /*if wrong detail is delivered*/
   \} else \{
        EmitTo(\$actor, "That is not a part that can be twisted in any direction.");
        EmitIn(\$actor."base:environment", Describe(\$actor) + " tries to twist parts of " +\$actor."base:possessive"+ " globe which are not designed for that particular manipulation.", \$actor);
   \}
   return TRUE;

/*IF SOMETHING PREVENTS THIS TO BE PERFORMED */
\} else \{ 
     EmitTo(\$actor, "Something in the mechanism of the globe prevents you to do this. Maybe if all other puzzles would be returned into the depth of the globe nothing would disturb this one from being solved.");
