Core:Properties.Core:Property:
         X[M] /* are they climbing down from the wall? */ 
if(\$startpartition == "up on the wall") \{ 
 EmitTo(\$actor, "You hop down from the wall."); 
 \$actor."room:partition" = \$targetpartition; 
 return TRUE; 
\} 
return FALSE;

         X[M] \$obs = Int(\$actor."skill:mental:observation") + Int(\$actor."career:mental:observation"); 

\$target = \$what[0]; 
if(NRefDetail(\$target) == "wall-north") \{ 
   if(\$obs \> 18) \{ 
        EmitTo(\$actor, "You notice marks of climbing on the north wall, they lead up to a bush with swaying leaves."); 
   \} else if(\$obs \> 10) \{ 
        EmitTo(\$actor, "There is a part on the north wall that is smoother then the otherwise rough stone, it leads to a bush having swaying leaves."); 
   \} else if(\$obs \> 7) \{ 
        EmitTo(\$actor, "You notice a bush with swaying leaves up on the wall."); 
   \}else if(\$obs \> 4) \{ 
        EmitTo(\$actor, "There is some vegetation on the north wall, hard to see what, it is not in reaching distance."); 
   \} return TRUE; 
\} else if(NRefDetail(\$target) == "default") \{ 
   if(\$obs \> 7) \{ 
        EmitTo(\$actor, "The north wall is particularly covered in shadow."); 
   \}else if(\$obs \> 4) \{ 
       EmitTo(\$actor, "The walls are more or less covered in shadow."); 
   \} return TRUE; 
\}

         X[M] /* climb up to the shadow crawler bush */ 
if(!\$dob \|\| sizeof(\$dob) != 1) return TRUE; 
 
if(NRefDetail(\$dob[0]) != "wall-north") return TRUE; 
 
\$agility = ironclaw::roll(\$roller: \$actor, \$bonus: "trait:agility"); 
\$agility += ironclaw::roll(\$roller: \$actor, \$bonus: "trait:body"); 
\$agility -= Int(\$actor."trait:body"); 
 
   if(\$agility \> 5) \{ 
        /* \$plant = \$\{[Ironclaw:Props:Flora-New:Plants:Other:Harvestable:Aloe]#88365\}; /* This is an old plant */
        \$plant = \$\{[Allegory:Props:Plants:A:AloeVera:AloeVeraPlant]#279858\}; 
        EmitTo(\$actor, "With rapid, agile motions you climb up to "+Describe(\$plant)+"."); 
        EmitIn(\$actor."base:environment", "With rapid, agile motions "+Describe(\$actor)+" climbs up to "+Describe(\$plant)+".", \$actor); 
        \$actor."room:partition" = "up on the wall"; 
        \$actor."base:proximity" = NewNRef(\$plant, "default"); 
        \$actor."base:stancestring" = "sitting"; 
        \$actor."base:prepositionstring" = "near"; 
   \} else if(\$agility \> 3) \{ 
        EmitTo(\$actor, "You start to climb on the rough surface of the wall not quite able to reach the vegetation. Your hands slip and you fall back. Thankfully the distance isn't that much and you safely land on your feet."); 
        EmitIn(\$actor."base:environment", Describe(\$actor)+" starts to climb the rough surface of the wall, not quite able to reach the vegetation that clings on the wall. "+capitalize(\$actor."base:possessive")+" fingers slip and fall back, landing on "+\$actor."base:possessive"+" feet.", \$actor); 
   \} else \{ 
       /* emit failure */ 
        EmitTo(\$actor, "You scrabble at the wall, before falling on "+Describe(NewNRef(\$actor, "rump"), nil, \$actor)+"."); 
        EmitIn(\$actor."base:environment", Describe(\$actor)+" scrabbles at the wall, before falling on "+Describe(NewNRef(\$actor, "rump"), \$actor)+".", \$actor); 
   \} 
return FALSE;
