Core:Properties.Core:Property:
         X[M] /* close the secret doors after a while */    
if (NRefDetail(\$target) == "exit-north") \{    
 Call(\$\{Lib:door\}, "self-closing", \$target: \$target, \$time: random(60*5) + 30);    
\} else if (NRefDetail(\$target) == "exit-northwest") \{    
 Call(\$\{Lib:door\}, "self-closing", \$target: \$target, \$time: random(60*5) + 30);    
\}    
return TRUE;

         X[M] \$delay(2, TRUE, "030f");
if(NRefDetail(\$target) == "exit-north") \{                      
 if(\$actor."trait:religion" \&\& \$actor."trait:religion" == "lutara") \{                      
  EmitTo(\$actor, "You feel a sense of comfort and all worries lessen as you enter this blessed place.");                      
  /*EmitIn(\$actor."base:environment", Describe(\$actor)+ " seems to step lightly and emits an aura of peace.", \$actor);*/    
  return TRUE;                      
 \} else if(\$actor."trait:religion" \&\& \$actor."trait:religion" == "sallumer") \{                      
  EmitTo(\$actor, "You gain a sense of foreboding and feel like an interloper on this sacred ground.");                      
  /*EmitIn(\$actor."base:environment", Describe(\$actor)+ " slumps noticeably.", \$actor);*/    
  return TRUE;                      
 \} else \{                      
  EmitTo(\$actor, "You feel as if you have stepped into a sacred area.");                      
  /*EmitIn(\$actor."base:environment", Describe(\$actor)+ " exhibits a moment of wonder across " +\$actor."base:possessive"+ " face.", \$actor);*/    
  return TRUE;                      
 \}    
\}                      
return TRUE;

         X[M] \$obs = Int(\$actor."skill:mental:observation") + Int(\$actor."career:mental:observation");    
    
\$target = \$what[0];    
if(NRefDetail(\$target) == "rocks") \{    
   if(\$obs \> 7) \{    
        EmitTo(\$actor, "You notice a patch of dark grass right under the rock that seems to move with its own space.");    
   \}else if(\$obs \> 4) \{    
        EmitTo(\$actor, "Something seems to be under the rock. Hard to see what exactly.");    
   \} return TRUE;    
\} else if(NRefDetail(\$target) == "default") \{    
   if(\$obs \> 7) \{    
        EmitTo(\$actor, "You note some dark vegetation under the rock that moves on its own.");    
   \}else if(\$obs \> 4) \{    
       EmitTo(\$actor, "You note some dark vegetation under the rock.");    
   \} return TRUE;    
\}

         X[M] if (NRefDetail(\$target) == "ducks")      
/* describe the ducks being fed */      
EmitIn(\$actor."base:environment", "The ducks automatically swarm to the food, fighting one another in a desperate attempt for a nibble.");       
return TRUE;

         X[M] /* opens the secret passage when you gesture symbolically at the vines */    
if (NRefDetail(\$target) == "vines") \{    
 if(\$adverb \&\& \$adverb == "symbolically") \{    
  if (this."details:exit-north:exit:closed") \{    
   this."details:exit-north:exit:closed" = 0;    
  \}    
 \}    
\}    
return TRUE;

         X[M] /* opens the secret passage to the cave when you push through the vines */    
    
/* disabled until halloween */    
if(time() \< 1225104265-60*60*7+60*60*24*4) \{    
 EmitTo(\$actor, "Although you remember the vision of pushing the vines aside, you sense it is too early. You allow the vines to fall back into place.");    
 EmitTo(\$actor, "[OOC: Try coming back on October 31st]");    
 return FALSE;    
\}    
    
/* can only open the vines if we know how */    
if(!\$actor."secrets:doorways:vines_to_cave") return TRUE;    
if (NRefDetail(\$target) == "vines") \{    
 if (this."details:exit-northwest:exit:closed")    
  this."details:exit-northwest:exit:closed" = 0;    
 \$witness_list = Match(\$actor."base:environment", "pc");    
 for(\$i=0;\$i\<sizeof(\$witness_list);\$i++) \{    
  \$witness = NRefOb(\$witness_list[\$i]);    
  if(\$witness != \$actor \&\& \$witness."udat:possessed" \&\& \$witness."udat:object" != \$actor."udat:object") \{    
   if(!\$witness."secrets:doorways:vines_to_cave")    
    \$witness."secrets:doorways:vines_to_cave" = 1;    
   if(!\$witness."secrets:doorways:vines_to_cave:teacher") \{    
    \$witness."secrets:doorways:vines_to_cave:teacher" = (\{ \$actor \});    
   \} else \{    
    \$witness."secrets:doorways:vines_to_cave:teacher" \|= (\{ \$actor \});    
   \}    
  \}    
 \}    
 Act(\$actor, "enter", \$what: NewNRef(\$this, "exit-northwest"));    
\}    
return FALSE;
