Core:Properties.Core:Property:
         X[M] /* Since this is a virtual room, close the door after the opener goes through */    
    
if(\$target \&\& (NRefDetail(\$target)=="exit-north")) \{    
  /* If we opened the door, close the guestroom door */    
  if(\$actor == \$this."details:exit-north:opener")    
    \$this."details:exit-north:exit:closed:local" = 1;    
\}    
    
    
return TRUE;

         X[M] /* Since this is a virtual room, record who opened the door */    
    
if(\$target \&\& (NRefDetail(\$target)=="exit-north"))    
  \$this."details:exit-north:opener" = \$actor;        
    
return TRUE;

         X[M] if(\$target \&\& NRefDetail(\$target)=="exit-north") \{ 
  EmitTo(\$actor, "You need a key to lock this door.");    
\}    
return FALSE;

         X[M] /* Since this is a virtual room, record who opened the door */    
    
if(\$target \&\& (NRefDetail(\$target)=="exit-north")) \{    
  if(!\$actor."virtualhome:avoirdupois:closet") \{    
    EmitTo(\$actor, Describe(\$target)+" is locked.");    
    return FALSE;    
  \}    
\}        
    
return TRUE;

         X[M] if(\$target \&\& NRefDetail(\$target)=="exit-north") \{   
  EmitTo(\$actor, "You need the right key to unlock this door.");   
\}   
return FALSE;
