Core:Properties.Core:Property:
         X[M] if(NRefDetail(\$dob[0]) == "carrot") \{

EmitTo(\$actor, "You dig your fingers into the ground, successfully latching onto and pulling up a plump, home-grown carrot."); EmitIn(\$actor."base:environment", Describe(\$actor)+" digs into the ground, successfully latching onto and pulling up a plump, home-grown carrot.", \$actor);

/* spawn the carrot*/     
\$carrot = Spawn(\$\{Allegory:Props:Plants:C:Carrot:CarrotRoot\});     
\$carrot."base:environment" = \$actor;     

\}

return TRUE;

         X[M] if(NRefDetail(\$dob[0]) == "potato") \{

EmitTo(\$actor, "You submerge your hands into the ground, successfully latching onto and pulling up a large, round potato."); EmitIn(\$actor."base:environment", Describe(\$actor)+" digs into the ground, successfully latching onto and pulling up a large, round potato.", \$actor);

/* spawn the potato*/     
\$potato = Spawn(\$\{Ironclaw:Props:Flora:Harvestable:Parts:P:Potato\});     
\$potato."base:environment" = \$actor;     

\}

return TRUE;

         X[M] if(NRefDetail(\$dob[0]) == "radish") \{

EmitTo(\$actor, "You pull upward on a batch of radishes, victoriously withdrawing one from the ground."); EmitIn(\$actor."base:environment", Describe(\$actor)+" pulls upward on a batch of radishes, victoriously withdrawing one from the ground.", \$actor);

/* spawn the radish*/     
\$radish = Spawn(\$\{Ironclaw:Props:Flora:Harvestable:Parts:R:Radish\});     
\$radish."base:environment" = \$actor;     

\}

return TRUE;
