Core:Properties.Core:Property:
         X[M] if(\$this."trait:safezone") \{
    \$actor."base:environment" = \$this."trait:safezone";
\} else \{
    EmitTo(\$actor, "[OOC: No safe room specified. Please file an @assist.]");
\}

return TRUE;

         X[M] /* This is a failsafe script to handle when a player is stuck in the ocean with no means of escape */
/* Are there any mounts in the room? */
\$inv = \$this."base:inventory";
for(\$i = 0; \$i \< sizeof(\$inv); \$i++) \{
    if(FindMerry(\$inv[\$i], "react", "mount-dob")) return TRUE;
\}

/* No mount was found. Offer an escape. */
interaction::question(\$question: "[OOC: Are you stuck?]", \$env: "TRUE", \$expires: 60, \$yes_ob: \$this, \$yes_fun: "unstick", \$no: "[OOC: Okay.]", \$expire: "[OOC: Okay.]", \$par: ([ "actor": \$actor ])); 

return FALSE;
